import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core/';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment';
import useStyles from './styles';
import { useDispatch } from 'react-redux';
import {deletePost,likeCount} from '../../../actions/posts';
const Post=({post,setCurrentID})=>{
    const classes=useStyles();
    const dispatch=useDispatch();
    return(
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={post.selectedFile} title={post.title}></CardMedia>
            <div className={classes.overlay}>
              <Typography variant="h6">{post.creator}</Typography>
              <Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
            </div>
            <div className={classes.overlay2}>
                <Button style={{color:'white'}} 
                    size="small" 
                    onClick={()=>{
                    setCurrentID(post._id)}
                    }>
                    <MoreHorizIcon fontSize="default"/>
                </Button>
            </div>
            <div className={classes.details}>
                <Typography variant="body2" color="textSecondary">
                    {post.tags.map((tag)=> (`#${tag} `))}
                </Typography>
            </div>
            <CardContent>
                <Typography variant="h5" gutterBottom classes={classes.title}>{post.title}</Typography>
            </CardContent>  
            <CardContent>
                <Typography variant="body2" color="textSecondary" component="p">{post.message}</Typography>
            </CardContent> 
            <CardActions className={classes.cardActions}>
                <Button 
                size="small" 
                color="primary"
                onClick={()=>{
                    dispatch(likeCount(post._id));
                }}>
                <ThumbUpAltIcon fontSize="small" /> Like {post.likeCount} </Button>
                <Button 
                    size="small" 
                    color="primary" 
                    onClick={()=>{dispatch(deletePost(post._id));}}>
                <DeleteIcon fontSize="small" /> Delete</Button>
            </CardActions> 
        </Card>
    )
}
export default Post;
